import * as React from "react";
import { cn } from "@/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";
import { BookA } from "lucide-react";

const buttonVariants = cva(
  "inline-flex items-center justify-center transition-colors rounded-md focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default:
          "bg-slate-800 text-white hover:bg-slate-700 px-4 py-2",
        icon:
          "bg-slate-800 text-white hover:bg-slate-700 h-9 w-9 rounded-full",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
);

export interface ButtonProps
  extends React.ComponentProps<"button">,
    VariantProps<typeof buttonVariants> {}

function Button({
  className,
  variant,
  children,
  ...props
}: ButtonProps) {
  return (
    <button
      data-slot="button"
      className={cn(buttonVariants({ variant }), className)}
      {...props}
    >
      {variant === "icon" ? (
        <BookA className="h-4 w-4" />
      ) : (
        children
      )}
    </button>
  );
}

export { Button };
